type BrandIconProps = {
  title: string;
  color?: string;
  path?: string;
  paths?: string[];
  viewBox?: string;
};

const BrandIcon = ({
  title,
  color = '#ffffff',
  path,
  paths,
  viewBox = '0 0 24 24',
}: BrandIconProps) => (
  <svg
    viewBox={viewBox}
    role="img"
    aria-label={`${title} logo`}
    className="h-6 w-6"
    style={{ color }}
  >
    <title>{title}</title>
    {(paths || (path ? [path] : [])).map((d, index) => (
      <path key={`${title}-path-${index}`} d={d} fill="currentColor" />
    ))}
  </svg>
);

